mapper.select:
SELECT z.*
FROM(SELECT rownum as rm, y.*
   FROM(SELECT x.*
    FROM(SELECT a.*, b.user_id
         FROM request_booklist a, user_data b
         WHERE a.user_keynum = b.user_keynum
         ORDER BY request_keynum desc
         )x


    WHERE title LIKE '%'||#{searchword}||'%'


    WHERE user_id LIKE '%'||#{searchword}||'%'


    )y
 )z
 WHERE z.rm BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE REQUEST_BOOKLIST
SET request_state='N', ref_date=sysdate
WHERE request_keynum=#{request_keynum}

UPDATE REQUEST_BOOKLIST
SET request_state='Y', ref_date=sysdate
WHERE request_keynum=#{request_keynum}

mapper.insert:
INSERT INTO booklist (book_keynum, isbn, title, author, publisher, pub_date, contents, thumbnail, receive_date, category_s, book_category, loan_state, borrow_state) SELECT booklist_book_keynum_seq.nextval, #{isbn}, #{title},
'미상' #{author} , #{publisher}, #{pub_date},
'도서정보 미제공' #{contents} , #{thumbnail, jdbcType=VARCHAR}, sysdate, k.category_s, k.book_category, 'Y', 'N' FROM kdc k WHERE k.category_s = #{category_s}
