mapper.select:
  	SELECT user_name, user_sex, user_age
	FROM user_data
	WHERE user_keynum = #{user_keynum}

SELECT a.*    
FROM(SELECT *
FROM booklist
WHERE book_keynum NOT IN (SELECT book_keynum
                            FROM user_booklist
                            WHERE user_keynum = #{user_keynum}
                            AND (borrow_state = '대출중' or borrow_state='반납'))
)a
WHERE isbn = #{isbn}
